import "./NewExpense.css";
import { useState } from "react";
function NewExpense() {
  const [enteredTitle, setEnteredTitle] = useState('');
  const [enteredAmount, setEnteredAmount] = useState(0);
//   const []
  function changeTitleHandler(event) {
    setEnteredTitle(event.target.value);
  }
  function changeAmountHandler(event){
    setEnteredAmount(event.target.value)
    console.log(event.target.value)
  }

  return (
    <form>
      <div className="form-input">
        <div className="form-input-title">
          <label>Title</label>
          <input type="text" onChange={changeTitleHandler}></input>
        </div>
        <div className="form-input-amount">
          <label>Amount</label>
          <input type="number" step="0.01" min="0" onChange={changeAmountHandler}></input>
        </div>
        <div className="form-input-date">
          <label>Date</label>
          <input type="date" value={new Date().toISOString.substr(0,10)}></input>
        </div>
      </div>
      <div className="form-submit-button">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
}
export default NewExpense;
