// isme sare expenses honge ...wo wala component hai ye
import ExpenseItem from "./ExpenseItem";
import { useState } from "react";
import './Expenses.css'
import Card from "../UI/Card";
import ExpenseFilter from "./ExpenseFilter";
function Expenses(props) {
  const expenses = props.items;  
  const [filterYear, setFilterYear] = useState(2020);
  function filterYearChangeHandler(enteredYear){
    setFilterYear(enteredYear);
  }
  return (
    <Card className="expenses">
      <ExpenseFilter selectedYear={filterYear} onFilterYearChange={filterYearChangeHandler}></ExpenseFilter>
      {
        expenses.map(expense => <ExpenseItem title={expense.title} amount={expense.amount} date={new Date(expense.date)}></ExpenseItem>)
      }
    </Card>
  );
}

export default Expenses;
